const UserChat = ({username,messages,AddNewMessage}) => {
    const isDisabled = () => {
        return true;
      };
    handleNewMessage = () => {
        AddNewMessage(username,message)
    }
    return(
        <div className="chat-window">
        <h2>Super Awesome Chat</h2>
        <div className="name sender">{username}</div>

        <ul className="message-list">
          {messages.map((message, index) => (
            <li
              key={index}
              className={
                message[index].username === username
                  ? "message sender"
                  : "message recipient"
              }
            >
              <p>{`${message[index].username}: ${message[index].text}`}</p>
            </li>
          ))}
        </ul>

        <div>
          <form className="input-group">
            <input
              type="text"
              className="form-control"
              placeholder="Enter your message..."
            />
            <div className="input-group-append">
              <button className="btn submit-button" disabled={isDisabled()}>
                SEND
              </button>
            </div>
          </form>
        </div>
      </div>
    )
}

export default UserChat
