import { Link} from "react-router-dom";
import    Book from "./Book"
import PropTypes from "prop-types";






const BookShelves =  ( { hanldeDetailClick, books ,MoveToShelve}) => {

const shelves = ["Read","currentlyReading","wantToRead"] 




const BookPerShelves =(shelf) =>{

   return(books.filter(book => book.shelf === shelf));
  };

 return (
    <div className="app">
        <div className="list-books">
          <div className="list-books-title">
            <h1>MyReads</h1>
          </div>
          <div className="list-books-content">
            <div>
            <div className="flex-container">
            {shelves.map(shelf => {
             return(  shelf === "none" ? ("") : 
              (<div className="bookshelf">

                <h2 className="bookshelf-title">{shelf}</h2>
                <div className="flex-child">
                  <ol className="books-grid">

                  {BookPerShelves(shelf).map((book) => {
                  return(<Book 
                        book = {book}
                        books={books}
                        MoveToShelve = {MoveToShelve} />)
                     })
                     }
                  </ol>
                </div>
              </div> ))
              })}
            </div>
            </div>
          </div>
          <div className="open-search">
            <Link to="/search">Add a book </Link>
          </div>
        </div>
    </div>
  );
}

BookShelves.propTypes = {
  books: PropTypes.array.isRequired,
  MoveToShelve: PropTypes.func.isRequired,
  userbooks: PropTypes.object,
};

export default BookShelves;
