import { useState } from "react";
const UserChat = ({username,messages,AddNewMessage}) => {
    const [value,setValue] = useState("")
    const HandleChange = (event) => {
        setValue(event.target.value)
    }
    const isDisabled = () => {
        return true;
      };
    const handleNewMessage = (event) => {
        event.preventDefault();
        const id = messages.length;
        const newMessage = {id:{"username": username , "text": value}};
        AddNewMessage(newMessage);
    }

    return(
        <div className="chat-window">
        <h2>Super Awesome Chat</h2>
        <div className="name sender">{username}</div>

        <ul className="message-list">
          {messages.map((message, index) => (
            <li
              key={index}
              className={
                message[index].username === username
                  ? "message sender"
                  : "message recipient"
              }
            >
              <p>{`${message[index].username}: ${message[index].text}`}</p>
            </li>
          ))}
        </ul>

        <div>
          <form className="input-group">
            <input onChange={HandleChange}
              type="text"
              className="form-control"
              placeholder="Enter your message..."
              value={value}
            />
            <div className="input-group-append">
              <button className="btn submit-button" disabled={isDisabled()}
              onClick={handleNewMessage}>
                Send
              </button>
            </div>
          </form>
        </div>
      </div>
    )
}

export default UserChat
