import { useState } from "react";

const User=({ username,user }) =>
{
   const [gameHide,setgameHide] = useState(true)
   const handlegameHide = () =>  {
    if (gameHide) {
     return ("${username} played * games")
     setgameHide(false)
    }
    else
      return ("${username} played ${user.games} games")
      setgameHide(true)
   }
    return (
        <div>
        <il> 
          <p> First Name : {user.firstName} </p>
          <p> Last Name : {user.lastName} </p>
          <button onClick={handlegameHide}> {handlegameHide} </button> 
        </il>
      </div>
      );


}

export default User
