/*
title str 
  subtitle str 
  authors list 
  publisher str 
  publishedDate str 
  description str 
  industryIdentifiers list 
  readingModes dict 
      text str 
      image str 
  pageCount int 
  printType str 
  categories list 
  averageRating int 
  ratingsCount int 
  maturityRating str 
  allowAnonLogging str 
  contentVersion str 
  panelizationSummary dict 
      containsEpubBubbles str 
      containsImageBubbles str 
  imageLinks dict 
      smallThumbnail str 
      thumbnail str 
  language str 
  previewLink str 
  infoLink str 
  canonicalVolumeLink str 
  id str 
  shelf str 
*/
import { useState } from "react";
import BookDetails from "./BookDetails";

const Shelveoptions = [
    {
      label: "None",
      value: "None",
    },
    {
      label: "Read",
      value: "Read",
    },
    {
      label: "currentlyReading",
      value: "currentlyReading",
    },
    {
      label: "wantToRead",
      value: "wantToRead",
    },
  ];

const Book = ({book,books,MoveToShelve}) => {

  const [showDetails, setShowDetails] = useState({"title": "None"}); 
  const hanldeDetailClick = (eventAction,title,bookDetails) => {
    if (eventAction === "show") {
       setShowDetails({"title": title ,"details": bookDetails})
    } else {
      setShowDetails({"title": "None"} )
    }
  };

  const isUserbook = books.filter(userbook => userbook.id === book.id ) ;
  const shelf = isUserbook.length > 0 ? 
                isUserbook.shelf : "None"


  const  handleSHeleveChange = ((book,event) => {
    event.preventDefault();
    book["shelf"] = event.target.value
    MoveToShelve(book);
    });

return(
    <li Key={book.id}>
       <div   className="book">
        <div className="book-top">
          <div
            className="book-cover"
            style={{
              width: 128,
              height: 193,
              backgroundImage:
              'url("'+book.imageLinks.thumbnail+'")'
            }}
          ></div>
          <div  className="book-shelf-changer">
            <select value={shelf} onChange={(event) => handleSHeleveChange(book,event)}>
               <option  value="none" disabled>
                                       Move to...
                                     </option>
               {Shelveoptions.map(shelve =>{
                 return (<option  value={shelve.value}>{shelve.label}</option>)}
                   )}
            </select>
          </div>
        </div>
        <div onClick={(event => hanldeDetailClick("show",book.title,book.description))} className="book-title" >{book.title}</div>
        <div className="book-authors">
            By : 
            {book.authors } 
        </div>
        {showDetails.title !== "None" ? 
      (<BookDetails  BookDetails={showDetails} hanldeDetailClick={hanldeDetailClick} />) 
       :
        ("")}
      </div>

    </li>)
    }
export default Book
