import Book from "./Book";
import { useState } from "react"
import { Link} from "react-router-dom";
import * as BooksAPI from "./BooksAPI";

const SearchBook = ({ books ,userbooks,MoveToShelve}) => {
     const [query_resutls,setQuery_results] = useState([])

    const [query,setQuery ] = useState("");

    const updateQuery = (query) => {
        setQuery(query.trim());
        const getBooks = async () => {
          const res = await BooksAPI.search(query.trim());
          setQuery_results(res);
        };
        query === "" ? [] : getBooks();

      };
      const searchBook = 
      async () => {
        const res = await BooksAPI.search(query);
        return(res);
      };

    const showingBooks =
        query === "" ? [] : SearchBook(query)


    return (
        <div>
          <div className="search-books">
          <div className="search-books-bar">
             <div className="search-books-input-wrapper">
              <input
                type="text"
                placeholder="Search by title, author, or ISBN"
                value={query}
                onChange={(event) => updateQuery(event.target.value)}
              />

          </div>
          <Link to="/" className="close-search">
           Close 
          </Link>
          </div>

          <div className="search-books-results">
             <ol className="books-grid">
              {query_resutls.map((book) => {

                return(<Book 
                        book = {book}
                        shelve = {userbooks[book.title] ? userbooks[book.title]  : "None"}
                        MoveToShelve = {MoveToShelve} />)
              })
              }
             </ol>
             </div>
        </div>
   </div>
    )
}

export default SearchBook ;
