import { useState } from "react";

const CreateUser=({ OnAddUser })=>
{
    const [valueFirstName,setvalueFirstName]  =  useState("");
    const [valueLastName,setvalueLastName]   =  useState("");
    const [valueUserName,setvalueUserName]   =  useState("");
    const handlevalueFirstNameChange = (event) =>{
        setvalueFirstName(event.target.value)
    };
    const handlevalueLastNameChange = (event) =>{
        setvalueLastName(event.target.value)
    };
    const handlevalueUserNameChange = (event) =>{
        setvalueUserName(event.target.value)
    };
    const addUser = (event) => {
        event.preventDefault();
        OnAddUser(valueFirstName,valueLastName,valueUserName);
        setvalueFirstName("");
        setvalueLastName("");
        setvalueUserName("");
      };
      const IsInputHasValues =() =>
      (valueFirstName ==="" || 
       valueLastName ===""|| 
       valueUserName ==="");

    return (
        <div>
        <form onSubmit={addUser}>
        <input
          type="text"
          placeholder="Enter First Name"
          value={valueFirstName}
          onChange={handlevalueFirstNameChange}
        />
        <span>  </span>
        <input
          type="text"
          placeholder="Enter Last Name"
          value={valueLastName}
          onChange={handlevalueLastNameChange}
        />
        <input
          type="text"
          placeholder="Enter username"
          value={valueUserName}
          onChange={handlevalueUserNameChange}
        />
        <button disabled={IsInputHasValues} >Add User</button>
      </form>
      </div>
      );


}

export default CreateUser
