import User from "./User";
import { useState } from "react";

const UserList=({ users }) => {
  const [showGamesPlayed,setshowGamesPlayed] = useState("*")
  const [buttonGamesPlayed,setsbuutonGamesPlayed] = useState("Show  the Number of Games Played")


  const handlegameHide = () =>  {
    if (setshowGamesPlayed !="*") {
     setsbuutonGamesPlayed("Hide the Number of Games Played")
    }
    else {
     setsbuutonGamesPlayed("Show the Number of Games Played")
    }
  }
    return (
        <div>
          <button onClick={handlegameHide}> {buttonGamesPlayed} </button>
        <ol> 
          {users.map(user =>{
            return(<User  user={user}  showGamesPlayed="*"/>);
          })

          }
        </ol>
      </div>
      );


}

export default UserList
