import { Link} from "react-router-dom";
import    Book from "./Book"


const BookShelves =  ( { books,userbooks ,MoveToShelve}) => {


 const shelves = ["Read","CurrentlyReading","WantToRead"] 

 const BookPerShelves =(shelve) =>{
    const shelvbook = Object.keys[userbooks].filter((title) => userbooks.title === shelve)
   // return(books.filter((book) => shelvbook.includes(book.title)));
   console.log(shelvbook)
   return([])
  }
 return (
    <div className="app">
        <div className="list-books">
          <div className="list-books-title">
            <h1>MyReads</h1>
          </div>
          <div className="list-books-content">

          {shelves.map(shelve => {
             return( 
              <ol className="books-grid">
              {BookPerShelves(shelve).map((book) => {

            return(<Book 
                    book = {book}
                    shelve = {userbooks[book.title] ? userbooks[book.title]  : "None"}
                    MoveToShelve = {MoveToShelve} />)
                 })
                 }
              </ol>
             )
              })}

          </div>
          <div className="open-search">
            <Link to="/search">Add a book </Link>
          </div>
        </div>
    </div>
  );
}


export default BookShelves

