import User from "./User";
import { useState } from "react";

const UserList=({ users }) => {
  const [showGamesPlayed,setshowGamesPlayed] = useState("Show")
  const handlegameHide = () =>  {
    if (showGamesPlayed === "Show") {
      setshowGamesPlayed("Hide")
    }
    else {
      setshowGamesPlayed("Show")
    }
  }

    return (
        <div>
          <button onClick={handlegameHide}> {showGamesPlayed} the Number of Games Played </button>
          <ol> 
          {users.map(user => {
            return(<User  user={user}  showGamesPlayed={showGamesPlayed} />)
          }
            )

          }
          </ol>
      </div>
      );

}

export default UserList
