  const [color, setColor] = useState("red");

  useEffect(() => {

    if (color = "red") {setTimeout(() => setColor("green"), 3000);}
    return (   setColor("green") );
  });

  return <p style={{ color }}>{color}</p>;
}
export default ChildComponent;
