import { useState } from "react";

const CreateUser=({ OnAddUser })=>
{

    const [user,setUser]   =  useState({
    "userName": "",
    "firstName": "",
    "lastname": ""})
    const HandleUserChange = (event) =>{
        const { name, value } = event.target;
        setUser({ ...user, [name]: value });
    };

    const handladdUser = (event) => {
       // event.valueFirstName.preventDefault();
      //  event.valueLastName.preventDefault();
        event.preventDefault();
        OnAddUser(user);
        setUser({
        "userName": "",
        "firstName": "",
        "lastName": ""});
        //setvalueLastName("");
        //setvalueUserName("");
      };
      const IsInputHasValues =() =>
      (user.firstName ==="" |
       user.lastName === ""|
       user.userName === "");

    return (
        <div>
        <form onSubmit={handladdUser}>

        <input
          type="text"
          placeholder="Enter First Name"
          value={user.firstName}
          onChange={HandleUserChange}
        /> 
        <br></br>
        <input
          type="text"
          placeholder="Enter Last Name"
          value={user.lastName}
          onChange={HandleUserChange}
        />
    <br></br>
         <input
          type="text"
          placeholder="Enter username"
          value={user.userName}
          onChange={HandleUserChange}
        />
<br></br>
        <button disabled={IsInputHasValues()} >Add User</button>
      </form>
      </div>
      );


}

export default CreateUser
